# A few aliases for eza, a ls replacement
alias l="eza --color-scale --color auto --sort Name"
alias ll="eza --color-scale --color auto --sort Name --long"
alias la="eza --color-scale --color auto --sort Name --long --all"
alias lr="eza --color-scale --color auto --sort Name --long --recurse"
alias lra="eza --color-scale --color auto --sort Name --long --recurse --all"
alias lt="eza --color-scale --color auto --sort Name --long --tree"
alias lta="eza --color-scale --color auto --sort Name --long --tree --all"

alias ls="eza --color-scale --color auto --sort Name"
alias tree="eza --color-scale --color auto --sort Name --tree"